<?php/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of dining * * @author Suharshana DsW */class Dining {    public $id;    public $type;    public $title;    public $price;    public $image_name;    public $short_description;    public $queue;    public function __construct($id) {        if ($id) {            $query = "SELECT * FROM `dining` WHERE `id`=" . $id;            $db = new Database();            $result = mysql_fetch_array($db->readQuery($query));            $this->id = $result['id'];            $this->type = $result['type'];            $this->title = $result['title'];            $this->price = $result['price'];            $this->image_name = $result['image_name'];            $this->short_description = $result['short_description'];            $this->queue = $result['queue'];            return $this;        }    }    public function create() {        $query = "INSERT INTO `dining` (`type`,`title`,`price`,`image_name`,`short_description`) VALUES  ('"                . $this->type . "','"                . $this->title . "', '"                . $this->price . "', '"                . $this->image_name . "', '"                . $this->short_description . "')";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            $last_id = mysql_insert_id();            return $this->__construct($last_id);        } else {            return FALSE;        }    }    public function all() {        $query = "SELECT * FROM `dining` ";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysql_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function getDiningByType($type) {        $query = "SELECT * FROM `dining` WHERE `type`= $type ORDER BY queue ASC";        $db = new Database();        $result = $db->readQuery($query);        $array_res = array();        while ($row = mysql_fetch_array($result)) {            array_push($array_res, $row);        }        return $array_res;    }    public function update() {        $query = "UPDATE  `dining` SET "                . "`title` ='" . $this->title . "', "                . "`price` ='" . $this->price . "', "                . "`image_name` ='" . $this->image_name . "', "                . "`short_description` ='" . $this->short_description . "', "                . "`queue` ='" . $this->queue . "' "                . "WHERE `id` = '" . $this->id . "'";        $db = new Database();        $result = $db->readQuery($query);        if ($result) {            return $this->__construct($this->id);        } else {            return FALSE;        }    }    public function delete() {        unlink(Helper::getSitePath() . "upload/dining/" . $this->image_name);        $query = 'DELETE FROM `dining` WHERE id="' . $this->id . '"';        $db = new Database();        return $db->readQuery($query);    }    public function arrange($key, $img) {        $query = "UPDATE `dining` SET `queue` = '" . $key . "'  WHERE id = '" . $img . "'";        $db = new Database();        $result = $db->readQuery($query);        return $result;    }}